package com.typecast;

public class Explicit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 130 ;
		byte b = (byte) x ;
		System.out.println(b);
		int y = 150;
		short s = (short) y;
		System.out.println(s);
		
		double d = 130.456;
		int i  =(int) d;
		byte b1= (byte)d;
		System.out.println(i);
		System.out.println(b1);
		
			}

}
